local forbidCloudsOverOcean = false 
-- This code will take a center tile (xc,yc) and a tile (x,y) and produce an integer K >=1 which is unique for each pair (x,y) (where (x,y) is a valid civ II tile coordinate, i.e. x,y are both even or both odd), given the same center tile (xc,yc).

-- Also, the process can be reversed.  Given K and a center tile (xc,yc), the original tile (x,y) can be found.  This is all for the same map, so z will always be the value of zc.

-- Process to get K given (x,y),(xc,yc):
-- n = (|x-xc|+|y-yc|)/2 
--     { 1 if y-yc > 0
-- p = { 1 if y-yc = 0 and x-xc < 0
--     { 0 otherwise
--
-- then
-- K = 1+4(n-1)n+2(x-xc + 2n) + p

-- Process to get (x,y,z) given K and (xc,yc,zc):
-- z = zc
-- if K == 1 then (x,y,z) = (xc,yc,zc)
-- Otherwise, use this procedure
-- To get n, find the n such that
-- 1+4(n-1)n<K<=1+4n(n+1)
-- L = K-1-4(n-1)n 
-- That means that L == 2(x-xc + 2n) + p
-- Therefore, p == L % 2 (since 2(x-xc + 2n) is a multiple of 2), so let
-- M = L - (L%2)
-- Hence, M == 2(x-xc+2n) and it follows that 
-- x = M/2 + xc - 2n
-- Using the relationship n == (|x-xc|+|y-yc|)/2 and the fact that p tells us if y>yc or y<yc,
-- we can find y (note that if y==yc, both options below yield y=yc) as follows:
-- if L%2 == 1 then y = 2n-|x-xc|+yc
-- if L%2 == 0 then y = |x-xc|-2n+yc
-- and thus we have found (x,y,z) from K,(xc,yc,zc)

local function getKey(tile,centerTile)
	local n = math.floor((math.abs(tile.x-centerTile.x)+math.abs(tile.y-centerTile.y))/2)
	local p = 0
	if tile.y > centerTile.y  or (tile.y == centerTile.y and tile.x < centerTile.x) then
		p = 1
	end
	local K = 1+4*(n-1)*n+2*(tile.x - centerTile.x + 2*n)+p
	return K
end

local function getTile(K,centerTile)
	local xc = centerTile.x
	local yc = centerTile.y
	local zc = centerTile.z
	if K == 1 then
		return centerTile
	end
	local n = 0
	while K > 1+4*n*(n+1) do
		n = n+1
	end
	local L = K -1-4*(n-1)*n
	local p = L%2
	local M = L - p
	local x = M//2 + xc-2*n
	local y = nil
	if p == 1 then
		y = 2*n-math.abs(x-xc)+yc
	else
		y = math.abs(x-xc)-2*n+yc
	end
	return civ.getTile(x,y,zc)
end
			
			
-- local function getTileXYZ(K,x,y,z)
-- for when the center might not be on the map 
local function getTileXYZ(K,xc,yc,zc)
	if K == 1 then
		return civ.getTile(xc,yc,zc)
	end
	local n = 0
	while K > 1+4*n*(n+1) do
		n = n+1
	end
	local L = K -1-4*(n-1)*n
	local p = L%2
	local M = L - p
	local x = M//2 + xc-2*n
	local y = nil
	if p == 1 then
		y = 2*n-math.abs(x-xc)+yc
	else
		y = math.abs(x-xc)-2*n+yc
	end
	return civ.getTile(x,y,zc)
end
local function getTileId (tile)
	if tile == nil then
		print("ERROR: \"getTileId\" function called with an invalid tile (input parameter is nil)")
		return nil
	end
	local mapWidth, mapHeight, mapQuantity = civ.getMapDimensions()
	local mapOffset = tile.z * mapWidth * mapHeight
	local tileOffset = tile.x + (tile.y * mapWidth)
	return mapOffset + tileOffset
end


