local function inClouds(unit)
    local location = unit.location
    if location.z == 0 then
        return false
    elseif location.z == 1 and location.terrainType % 16 == cloudTerrainHigh then
        return true
    elseif location.z == 2 and location.terrainType % 16 == cloudTerrainNight then
        return true
    else
        return false
    end
end

