	local ussrNukePayment = specialNumbers.bonusPerNuke
	local ussrNukePenalty = math.abs ((ussrNukes-specialNumbers.nuclearTreatyLimitation) * specialNumbers.penaltyPerNuke)
	local usaNukePayment = specialNumbers.bonusPerNuke
