	local indiaNukePayment = specialNumbers.bonusPerNuke
	local indiaNukePenalty = math.abs (indiaNukes-specialNumbers.nuclearTreatyLimitation * specialNumbers.penaltyPerNuke)
	
	if turn >=1 and turn <= 135 and tribe == object.tUSSR and state.USSRsignedNonProliferationTreaty == true then 
			if ussrNukes <= specialNumbers.nuclearTreatyLimitation then 
			object.tUSSR.money = object.tUSSR.money + ussrNukePayment
			--civ.ui.text(func.splitlines("Test to see if this is doing anything and "..tostring(ussrNukes).." Did this tell us how many nukes Soviets have?"))
