			object.tUSA.money = object.tUSA.money - usaNukePenalty
			end 
		end
		
		if 	tribe == object.tEurope and state.EUROPEsignedNonProliferationTreaty == true then 
			if euroNukes <= specialNumbers.nuclearTreatyLimitation then 
			object.tEurope.money = object.tEurope.money + euroNukePayment
			elseif euroNukes >= specialNumbers.nuclearTreatyLimitation then
			civ.ui.text(func.splitlines("The European Community is in violation of the Strategic Arms Limitation Treaty.  While the Europeans are allowed " ..tostring(specialNumbers.nuclearTreatyLimitation).." nuclear weapons or delivery systems (such as strategic bombers and submarines), Europe has " ..tostring(euroNukes).." in its inventory at this time.  The European Community will pay a penalty of " ..tostring(euroNukePenalty)..",000 gold as a result."))
			object.tEurope.money = object.tEurope.money - euroNukePenalty
			end 
		end
		
		if 	tribe == object.tChina and state.CHINAsignedNonProliferationTreaty == true then 
			if chinaNukes <= specialNumbers.nuclearTreatyLimitation then 
			object.tChina.money = object.tChina.money + chinaNukePayment
			elseif chinaNukes >= specialNumbers.nuclearTreatyLimitation then
			civ.ui.text(func.splitlines("China is in violation of the Strategic Arms Limitation Treaty.  While the Chinese are allowed " ..tostring(specialNumbers.nuclearTreatyLimitation).." nuclear weapons or delivery systems (such as strategic bombers and submarines), China has " ..tostring(chinaNukes).." in its inventory at this time.  China will pay a penalty of " ..tostring(chinaNukePenalty)..",000 gold as a result."))
			object.tChina.money = object.tChina.money - chinaNukePenalty
			end 
		end
		
		if 	tribe == object.tIndia and state.INDIAsignedNonProliferationTreaty == true then 
			if indiaNukes <= specialNumbers.nuclearTreatyLimitation then 
			object.tIndia.money = object.tIndia.money + indiaNukePayment
			elseif indiaNukes >= specialNumbers.nuclearTreatyLimitation then
