	local usaTooManyCitiesPenalty = 500
	if turn >=1 and turn <= 135 and tribe == object.tUSA and usaCities >= 36 and usaCities <=37 and object.tUSA.isHuman then 
	usaTooManyCitiesPenalty = 500
	object.tUSA.money = math.max(object.tUSA.money - usaTooManyCitiesPenalty, 500)
	civ.ui.text(func.splitlines("As a democratic republic, conquering foreign lands is unsightly.  We should transfer our territorial gains to our Pro-Western proxy by pressing '2' and gifting them cities.  If we don't, we will lose " ..tostring(usaTooManyCitiesPenalty).." gold every turn.  Taking more cities will increase this number.  As an alternative, we can build some military bases which will not penalize us."))
	elseif turn >=1 and turn <= 135 and tribe == object.tUSA and usaCities >= 37 and usaCities <=38 and object.tUSA.isHuman then 
	usaTooManyCitiesPenalty = 1500
	object.tUSA.money = math.max(object.tUSA.money - usaTooManyCitiesPenalty, 500)
	civ.ui.text(func.splitlines("As a democratic republic, conquering foreign lands is unsightly.  We should transfer our territorial gains to our Pro-Western proxy by pressing '2' and gifting them cities.  If we don't, we will lose " ..tostring(usaTooManyCitiesPenalty).." gold every turn.  Taking more cities will increase this number.  As an alternative, we can build some military bases which will not penalize us."))
	elseif turn >=1 and turn <= 135 and tribe == object.tUSA and usaCities >= 39 and usaCities <=40 and object.tUSA.isHuman then 
	usaTooManyCitiesPenalty = 4500
	object.tUSA.money = math.max(object.tUSA.money - usaTooManyCitiesPenalty, 500)
	civ.ui.text(func.splitlines("As a democratic republic, conquering foreign lands is unsightly.  We should transfer our territorial gains to our Pro-Western proxy by pressing '2' and gifting them cities.  If we don't, we will lose " ..tostring(usaTooManyCitiesPenalty).." gold every turn.  Taking more cities will increase this number.  As an alternative, we can build some military bases which will not penalize us."))
	elseif turn >=1 and turn <= 135 and tribe == object.tUSA and usaCities >= 41 and object.tUSA.isHuman then 
	usaTooManyCitiesPenalty = 13500
	object.tUSA.money = math.max(object.tUSA.money - usaTooManyCitiesPenalty, 500)
	civ.ui.text(func.splitlines("As a democratic republic, conquering foreign lands is unsightly.  We should transfer our territorial gains to our Pro-Western proxy by pressing '2' and gifting them cities.  If we don't, we will lose " ..tostring(usaTooManyCitiesPenalty).." gold every turn.  Taking more cities will increase this number.  As an alternative, we can build some military bases which will not penalize us."))
	
	
	end 

end 



