local function isDirectionKey(keyID)
    if keyID >= 192 and keyID <=199 then
        return true
    elseif keyID >= 161 and keyID <= 169 and keyID ~= 165 then
        return true
    elseif keyID == 78 then
        return true
    else
        return false
    end
end

