local function afterProduction(turn,tribe)

diplomacySettings.clearCityDeployments(tribe)
weaponSales.verifyOffers(tribe)

if turn == 1 and tribe == object.tUSA and object.tUSA.isHuman then 
	civ.ui.text(func.splitlines("Did this work as intended?"))
end 

if turn == 45 and (tribe == object.tUSSR) then 
	civ.ui.text(func.splitlines("Eager to erase painful memories of the dictator forever, the Soviets renamed Stalin's city 'Volgograd.'"))
	object.cStalingrad.name = "Volgograd"
end 

if turn == 52 and (tribe == object.tUSA) then 
	civ.ui.text(func.splitlines("The Post-War Baby Boom has ended in the United States as individuals are less confident in growing large families."))
	civ.giveTech(object.tUSA,object.aEndOfBabyBoom)
end


if turn >=1 and turn <=135 and civ.hasTech(object.tEurope, civ.getTech(58)) and (tribe == object.tEurope) then
	justOnce("European Alliance with USA Ends", function()
	diplomacy.clearAlliance(object.tUSA,object.tEurope)
	civ.ui.text(func.splitlines("The nations of Europe feel confident enough in their post-war recovery to abandon their alliance with the United States.  Europe declares she has retaken her place as a superpower in the world, and will be the master of her own fate.  The United States and Europe may now directly fight each other."))
end)
end

if turn >=1 and turn <=135 and civ.hasTech(object.tEurope, civ.getTech(58)) and (tribe == object.tUSA) then
	justOnce("USA Learns of End to European Alliance", function()
	civ.ui.text(func.splitlines("The United States ambassador issues a protest to the end of the European alliance while policymakers in Washington debate how to respond."))
end)
end

if turn >=1 and turn <=135 and civ.hasTech(object.tChina, civ.getTech(35)) and (tribe == object.tChina)  then
	justOnce("The Sino-Soviet Split Chinese Perspective", function()
	diplomacy.clearAlliance(object.tUSSR,object.tChina)
	civ.ui.text(func.splitlines("Beijing feels confident enough to abandon their alliance with the Soviet Union in what will become known as the Sino-Soviet Split.  While both sides remain generally friendly--for now--future conflicts may lead directly to war."))
end)
end

if turn >=1 and turn <=135 and civ.hasTech(object.tChina, civ.getTech(35)) and (tribe == object.tUSSR)  then
	justOnce("The Sino-Soviet Split Russian Perspective", function()
	diplomacy.clearAlliance(object.tUSSR,object.tChina)
	civ.ui.text(func.splitlines("Moscow receives the news that China has abandoned the Sino-Soviet alliance with dismay as the Kremlin debates how to react."))
end)
end

