civ.scen.onUnitKilled(function (defender, attacker)
if defender.type == object.uNAsianNat and attacker.owner == object.tChina and math.random() < 0.5 then
		civ.ui.text(func.splitlines("A brigade of Nationalist soldiers is defeated in battle.  Disenchanted with their leaders, and faced with few better prospects, many choose to switch sides and fight for the Communists."))
		civlua.createUnit(object.uChineseInf, object.tChina, {{attacker.location.x,attacker.location.y,attacker.location.z}}, {randomize=false, veteran=false})
		object.tChina.money = object.tChina.money + 25
	end
	
if defender.type == object.uTerrorist then
		civ.ui.text(func.splitlines("A ".. tostring(attacker.owner.adjective) .." Special Forces unit eliminates a terrorist cell! Valuable intels is recovered allowing the several terrorist assets to be frozen and seized! "))
		civ.getCurrentTribe().money = civ.getCurrentTribe().money + 1000
	end
	
if attacker.type == object.uF14Tomcat then
	justOnce("Kenny Loggins", function()
		civ.ui.text(func.splitlines("An ".. tostring(attacker.owner.adjective) .." F-14 Tomcat shoots down an enemy fighter! You might even say they've entered the 'Danger Zone'! "))
	end)
	end
	
end) -- Ends the onUnitKilled function

